import React, { useState, useEffect, useRef } from 'react';
import { clothingSizes, SizeMapping } from '../data/sizingData';
import './ProfileBuilder.css';

interface FormData {
  height: string;
  weight: string;
  heightUnit: 'cm' | 'ft';
  weightUnit: 'kg' | 'lbs';
  selectedSize: string;
}

const ProfileBuilder: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState<FormData>({
    height: '',
    weight: '',
    heightUnit: 'cm',
    weightUnit: 'kg',
    selectedSize: '',
  });

  const sizeTableRef = useRef<HTMLDivElement>(null);

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleNext = () => {
    if (currentStep === 1) {
      // Calculate suggested size before moving to next step
      const suggestedSize = getSuggestedSize();
      setFormData((prev) => ({
        ...prev,
        selectedSize: suggestedSize,
      }));
    }
    setCurrentStep((prev) => prev + 1);
  };

  const handleBack = () => {
    setCurrentStep((prev) => prev - 1);
  };

  const isStepValid = () => {
    if (currentStep === 1) {
      return formData.height !== '' && formData.weight !== '';
    }
    return true;
  };

  // Convert height to cm if in feet
  const getHeightInCm = (): number => {
    const height = parseFloat(formData.height);
    return formData.heightUnit === 'ft' ? height * 30.48 : height;
  };

  // Convert weight to kg if in lbs
  const getWeightInKg = (): number => {
    const weight = parseFloat(formData.weight);
    return formData.weightUnit === 'lbs' ? weight * 0.453592 : weight;
  };

  // Simple BMI-based size suggestion
  const getSuggestedSize = (): string => {
    const heightInCm = getHeightInCm();
    const weightInKg = getWeightInKg();

    if (!heightInCm || !weightInKg) return 'M';

    const bmi = weightInKg / Math.pow(heightInCm / 100, 2);

    if (bmi < 18.5) return 'XS';
    if (bmi < 25) return 'M';
    if (bmi < 30) return 'L';
    return 'XL';
  };

  const handleSizeSelect = (size: string) => {
    setFormData((prev) => ({
      ...prev,
      selectedSize: size,
    }));
  };

  useEffect(() => {
    // Scroll to suggested size when step 2 is loaded
    if (currentStep === 2 && sizeTableRef.current) {
      const selectedRow =
        sizeTableRef.current.querySelector('.size-row.selected');
      if (selectedRow) {
        selectedRow.scrollIntoView({ block: 'center', behavior: 'smooth' });
      }
    }
  }, [currentStep]);

  const renderStep1 = () => (
    <div className='form-step'>
      <div className='measurement-group'>
        <label>Height</label>
        <div className='input-with-unit'>
          <input
            type='number'
            name='height'
            value={formData.height}
            onChange={handleInputChange}
            placeholder='Enter height'
            min='0'
          />
          <select
            name='heightUnit'
            value={formData.heightUnit}
            onChange={handleInputChange}
          >
            <option value='cm'>cm</option>
            <option value='ft'>ft</option>
          </select>
        </div>
      </div>

      <div className='measurement-group'>
        <label>Weight</label>
        <div className='input-with-unit'>
          <input
            type='number'
            name='weight'
            value={formData.weight}
            onChange={handleInputChange}
            placeholder='Enter weight'
            min='0'
          />
          <select
            name='weightUnit'
            value={formData.weightUnit}
            onChange={handleInputChange}
          >
            <option value='kg'>kg</option>
            <option value='lbs'>lbs</option>
          </select>
        </div>
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className='form-step'>
      <div className='size-selection'>
        <h3>Suggested Size: {formData.selectedSize}</h3>
        <p className='size-instruction'>
          Scroll to view more sizes and select the one that fits you best
        </p>

        <div className='size-table-container' ref={sizeTableRef}>
          <table className='size-table'>
            <thead>
              <tr>
                <th>Size</th>
                <th>US</th>
                <th>UK</th>
                <th>EU</th>
                <th>IT</th>
              </tr>
            </thead>
            <tbody>
              {clothingSizes.map((size: SizeMapping) => (
                <tr
                  key={`${size.descriptive}-${size.us}`}
                  className={`size-row ${
                    size.descriptive === formData.selectedSize ? 'selected' : ''
                  }`}
                  onClick={() => handleSizeSelect(size.descriptive)}
                >
                  <td>{size.descriptive}</td>
                  <td>{size.us}</td>
                  <td>{size.uk}</td>
                  <td>{size.eu}</td>
                  <td>{size.it}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  return (
    <div className='profile-builder'>
      <div className='profile-container'>
        <div className='wizard-header'>
          <h2>Create Your Size Profile</h2>
          <div className='step-indicators'>
            <div
              className={`step-indicator ${currentStep === 1 ? 'active' : ''}`}
            >
              <div className='step-number'>1</div>
              <div className='step-label'>Basic Measurements</div>
            </div>
            <div
              className={`step-indicator ${currentStep === 2 ? 'active' : ''}`}
            >
              <div className='step-number'>2</div>
              <div className='step-label'>Size Selection</div>
            </div>
            <div
              className={`step-indicator ${currentStep === 3 ? 'active' : ''}`}
            >
              <div className='step-number'>3</div>
              <div className='step-label'>Preferences</div>
            </div>
          </div>
        </div>

        <form className='wizard-form' onSubmit={(e) => e.preventDefault()}>
          {currentStep === 1 && renderStep1()}
          {currentStep === 2 && renderStep2()}

          <div className='form-navigation'>
            {currentStep > 1 && (
              <button
                type='button'
                className='back-button'
                onClick={handleBack}
              >
                Back
              </button>
            )}
            <button
              type='button'
              className='next-button'
              onClick={handleNext}
              disabled={!isStepValid()}
            >
              {currentStep === 3 ? 'Finish' : 'Next Step'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProfileBuilder;
