import React, { useState, useEffect, useRef } from 'react';
import { sizeData, SizeMapping } from '../data/sizingData';
import { Profile } from '../types/profile';
import './ProfileBuilder.css';

interface FormData {
  height: string;
  weight: string;
  heightUnit: 'cm' | 'ft';
  weightUnit: 'kg' | 'lbs';
  selectedSize: string;
  name: string;
  gender: 'male' | 'female';
}

const ProfileBuilder: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState<FormData>({
    height: '',
    weight: '',
    heightUnit: 'cm',
    weightUnit: 'kg',
    selectedSize: '',
    name: '',
    gender: 'male',
  });
  const [profiles, setProfiles] = useState<Profile[]>([]);
  const sizeTableRef = useRef<HTMLDivElement>(null);

  // Load saved profiles on component mount
  useEffect(() => {
    const savedProfiles = localStorage.getItem('sizeProfiles');
    if (savedProfiles) {
      setProfiles(JSON.parse(savedProfiles));
    }
  }, []);

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleNext = () => {
    if (currentStep === 1) {
      const suggestedSize = getSuggestedSize();
      setFormData((prev) => ({
        ...prev,
        selectedSize: suggestedSize,
      }));
    }
    setCurrentStep((prev) => prev + 1);
  };

  const handleBack = () => {
    setCurrentStep((prev) => prev - 1);
  };

  const isStepValid = () => {
    if (currentStep === 1) {
      return formData.height !== '' && formData.weight !== '';
    }
    if (currentStep === 3) {
      return formData.name.trim() !== '';
    }
    return true;
  };

  const getHeightInCm = (): number => {
    const height = parseFloat(formData.height);
    return formData.heightUnit === 'ft' ? height * 30.48 : height;
  };

  const getWeightInKg = (): number => {
    const weight = parseFloat(formData.weight);
    return formData.weightUnit === 'lbs' ? weight * 0.453592 : weight;
  };

  const getSuggestedSize = (): string => {
    const heightInCm = getHeightInCm();
    const weightInKg = getWeightInKg();

    if (!heightInCm || !weightInKg) return 'M';

    const bmi = weightInKg / Math.pow(heightInCm / 100, 2);

    if (bmi < 18.5) return 'XS';
    if (bmi < 25) return 'M';
    if (bmi < 30) return 'L';
    return 'XL';
  };

  const handleSizeSelect = (size: string) => {
    setFormData((prev) => ({
      ...prev,
      selectedSize: size,
    }));
  };

  const handleSaveProfile = () => {
    const newProfile: Profile = {
      id: Date.now().toString(),
      name: formData.name,
      gender: formData.gender,
      measurements: {
        height: formData.height,
        weight: formData.weight,
        heightUnit: formData.heightUnit,
        weightUnit: formData.weightUnit,
      },
      selectedSize: formData.selectedSize,
      createdAt: new Date().toISOString(),
    };

    const updatedProfiles = [...profiles, newProfile];
    setProfiles(updatedProfiles);
    localStorage.setItem('sizeProfiles', JSON.stringify(updatedProfiles));

    // Reset form and go back to step 1
    setFormData({
      height: '',
      weight: '',
      heightUnit: 'cm',
      weightUnit: 'kg',
      selectedSize: '',
      name: '',
      gender: 'male',
    });
    setCurrentStep(1);
  };

  const handleDeleteProfile = (id: string) => {
    const updatedProfiles = profiles.filter((profile) => profile.id !== id);
    setProfiles(updatedProfiles);
    localStorage.setItem('sizeProfiles', JSON.stringify(updatedProfiles));
  };

  const renderStep1 = () => (
    <div className='form-step'>
      <div className='gender-selection'>
        <label>Gender</label>
        <div className='radio-group'>
          <label className='radio-label'>
            <input
              type='radio'
              name='gender'
              value='male'
              checked={formData.gender === 'male'}
              onChange={handleInputChange}
            />
            Male
          </label>
          <label className='radio-label'>
            <input
              type='radio'
              name='gender'
              value='female'
              checked={formData.gender === 'female'}
              onChange={handleInputChange}
            />
            Female
          </label>
        </div>
      </div>

      <div className='measurement-group'>
        <label>Height</label>
        <div className='input-with-unit'>
          <input
            type='number'
            name='height'
            value={formData.height}
            onChange={handleInputChange}
            placeholder='Enter height'
            min='0'
          />
          <select
            name='heightUnit'
            value={formData.heightUnit}
            onChange={handleInputChange}
          >
            <option value='cm'>cm</option>
            <option value='ft'>ft</option>
          </select>
        </div>
      </div>

      <div className='measurement-group'>
        <label>Weight</label>
        <div className='input-with-unit'>
          <input
            type='number'
            name='weight'
            value={formData.weight}
            onChange={handleInputChange}
            placeholder='Enter weight'
            min='0'
          />
          <select
            name='weightUnit'
            value={formData.weightUnit}
            onChange={handleInputChange}
          >
            <option value='kg'>kg</option>
            <option value='lbs'>lbs</option>
          </select>
        </div>
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className='form-step'>
      <div className='size-selection'>
        <h3>Suggested Size: {formData.selectedSize}</h3>
        <p className='size-instruction'>
          Scroll to view more sizes and select the one that fits you best
        </p>

        <div className='size-table-container' ref={sizeTableRef}>
          <table className='size-table'>
            <thead>
              <tr>
                <th>Size</th>
                <th>US</th>
                <th>UK</th>
                <th>EU</th>
                <th>IT</th>
              </tr>
            </thead>
            <tbody>
              {sizeData.women.map((size: SizeMapping) => (
                <tr
                  key={`${size.descriptive}-${size.us}`}
                  className={`size-row ${
                    size.descriptive === formData.selectedSize ? 'selected' : ''
                  }`}
                  onClick={() => handleSizeSelect(size.descriptive)}
                >
                  <td>{size.descriptive}</td>
                  <td>{size.us}</td>
                  <td>{size.uk}</td>
                  <td>{size.eu}</td>
                  <td>{size.it}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className='form-step'>
      <div className='profile-details'>
        <div className='input-group'>
          <label htmlFor='name'>Name</label>
          <input
            type='text'
            id='name'
            name='name'
            value={formData.name}
            onChange={handleInputChange}
            placeholder='Enter your name'
            required
          />
        </div>
      </div>
    </div>
  );

  const renderSavedProfiles = () => (
    <div className='saved-profiles'>
      <h3>Saved Profiles</h3>
      {profiles.length === 0 ? (
        <p className='no-profiles'>No saved profiles yet</p>
      ) : (
        <div className='profiles-grid'>
          {profiles.map((profile) => (
            <div key={profile.id} className='profile-card'>
              <div className='profile-card-header'>
                <h4>{profile.name}</h4>
                <button
                  className='delete-profile'
                  onClick={() => handleDeleteProfile(profile.id)}
                  title='Delete profile'
                >
                  Ã—
                </button>
              </div>
              <div className='profile-card-body'>
                <p>Gender: {profile.gender}</p>
                <p>Size: {profile.selectedSize}</p>
                <p>
                  Height: {profile.measurements.height}{' '}
                  {profile.measurements.heightUnit}
                </p>
                <p>
                  Weight: {profile.measurements.weight}{' '}
                  {profile.measurements.weightUnit}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div className='profile-builder'>
      <div className='profile-container'>
        <div className='wizard-header'>
          <h2>Create Your Size Profile</h2>
          <div className='step-indicators'>
            <div
              className={`step-indicator ${currentStep === 1 ? 'active' : ''}`}
            >
              <div className='step-number'>1</div>
              <div className='step-label'>Basic Measurements</div>
            </div>
            <div
              className={`step-indicator ${currentStep === 2 ? 'active' : ''}`}
            >
              <div className='step-number'>2</div>
              <div className='step-label'>Size Selection</div>
            </div>
            <div
              className={`step-indicator ${currentStep === 3 ? 'active' : ''}`}
            >
              <div className='step-number'>3</div>
              <div className='step-label'>Profile Details</div>
            </div>
          </div>
        </div>

        <form className='wizard-form' onSubmit={(e) => e.preventDefault()}>
          {currentStep === 1 && renderStep1()}
          {currentStep === 2 && renderStep2()}
          {currentStep === 3 && renderStep3()}

          <div className='form-navigation'>
            {currentStep > 1 && (
              <button
                type='button'
                className='back-button'
                onClick={handleBack}
              >
                Back
              </button>
            )}
            {currentStep < 3 ? (
              <button
                type='button'
                className='next-button'
                onClick={handleNext}
                disabled={!isStepValid()}
              >
                Next Step
              </button>
            ) : (
              <button
                type='button'
                className='save-button'
                onClick={handleSaveProfile}
                disabled={!isStepValid()}
              >
                Save Profile
              </button>
            )}
          </div>
        </form>

        {renderSavedProfiles()}
      </div>
    </div>
  );
};

export default ProfileBuilder;
