import React, { useState } from 'react';
import { regions, Region, sizeData, SizeMapping } from '../data/sizingData';
import './SizeConverter.css';

type Gender = 'men' | 'women';
type Step = 'region' | 'measurements';

const SizeConverter: React.FC = () => {
  const [step, setStep] = useState<Step>('region');
  const [selectedRegion, setSelectedRegion] = useState<Region | ''>('');
  const [selectedGender, setSelectedGender] = useState<Gender>('women');
  const [height, setHeight] = useState<string>('');
  const [weight, setWeight] = useState<string>('');

  const handleRegionSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (selectedRegion) {
      setStep('measurements');
    }
  };

  const getSuggestedSize = (
    height: string,
    weight: string
  ): SizeMapping | null => {
    // This is a simple example. You might want to implement more sophisticated logic
    const sizes = sizeData[selectedGender];
    const bmi = calculateBMI(parseFloat(height), parseFloat(weight));

    if (isNaN(bmi)) return null;

    if (bmi < 18.5) return sizes[0]; // XXS-XS
    if (bmi < 20) return sizes[1]; // XS-S
    if (bmi < 22) return sizes[2]; // S
    if (bmi < 25) return sizes[3]; // M
    if (bmi < 28) return sizes[4]; // L
    if (bmi < 31) return sizes[5]; // XL
    if (bmi < 34) return sizes[6]; // XXL
    return sizes[7]; // XXXL
  };

  const calculateBMI = (height: number, weight: number): number => {
    // Height in cm, weight in kg
    const heightInMeters = height / 100;
    return weight / (heightInMeters * heightInMeters);
  };

  if (step === 'region') {
    return (
      <div className='size-converter'>
        <h2>Size Guide</h2>
        <form onSubmit={handleRegionSubmit} className='region-form'>
          <div className='form-group'>
            <label htmlFor='gender'>Gender:</label>
            <div className='gender-buttons'>
              <button
                type='button'
                className={`gender-button ${
                  selectedGender === 'women' ? 'active' : ''
                }`}
                onClick={() => setSelectedGender('women')}
              >
                Women
              </button>
              <button
                type='button'
                className={`gender-button ${
                  selectedGender === 'men' ? 'active' : ''
                }`}
                onClick={() => setSelectedGender('men')}
              >
                Men
              </button>
            </div>
          </div>

          <div className='form-group'>
            <label htmlFor='region'>Your Region:</label>
            <select
              id='region'
              value={selectedRegion}
              onChange={(e) => setSelectedRegion(e.target.value as Region)}
              required
            >
              <option value=''>Select your region</option>
              {regions.map((region) => (
                <option key={region.id} value={region.id}>
                  {region.label}
                </option>
              ))}
            </select>
          </div>

          <button
            type='submit'
            className='next-button'
            disabled={!selectedRegion}
          >
            Next
          </button>
        </form>
      </div>
    );
  }

  return (
    <div className='size-converter'>
      <h2>Size Guide</h2>
      <div className='measurements-form'>
        <div className='form-group'>
          <label htmlFor='height'>Height (cm):</label>
          <input
            type='number'
            id='height'
            value={height}
            onChange={(e) => setHeight(e.target.value)}
            placeholder='Enter height in cm'
            required
          />
        </div>

        <div className='form-group'>
          <label htmlFor='weight'>Weight (kg):</label>
          <input
            type='number'
            id='weight'
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
            placeholder='Enter weight in kg'
            required
          />
        </div>

        {height && weight && (
          <div className='size-suggestion'>
            <h3>Suggested Size</h3>
            {(() => {
              const suggestedSize = getSuggestedSize(height, weight);
              if (!suggestedSize) return <p>Please enter valid measurements</p>;

              return (
                <div className='size-details'>
                  <p className='descriptive-size'>
                    Descriptive Size:{' '}
                    <strong>{suggestedSize.descriptive}</strong>
                  </p>
                  <p className='regional-size'>
                    {regions.find((r) => r.id === selectedRegion)?.label} Size:{' '}
                    <strong>
                      {selectedRegion ? suggestedSize[selectedRegion] : ''}
                    </strong>
                  </p>
                  <div className='size-table'>
                    <h4>Size Equivalents</h4>
                    <div className='size-grid'>
                      {regions.map((region) => (
                        <div key={region.id} className='size-row'>
                          <span className='region-label'>{region.label}:</span>
                          <span className='size-value'>
                            {suggestedSize[region.id]}
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              );
            })()}
          </div>
        )}

        <button
          type='button'
          className='back-button'
          onClick={() => setStep('region')}
        >
          Back to Region Selection
        </button>
      </div>
    </div>
  );
};

export default SizeConverter;
