import React, { useState } from 'react';
import { SizeSystem, getAvailableSizes, convertSize } from '../data/sizingData';
import './SizeConverter.css';

const SizeConverter: React.FC = () => {
  const [selectedSystem, setSelectedSystem] = useState<SizeSystem>('us');
  const [selectedSize, setSelectedSize] = useState<string>('');

  const sizeSystemLabels: Record<SizeSystem, string> = {
    descriptive: 'Letter Size',
    us: 'US Size',
    uk: 'UK Size',
    eu: 'EU Size',
    it: 'Italian Size',
  };

  const availableSizes = getAvailableSizes(selectedSystem);

  const getConversions = () => {
    if (!selectedSize) return null;

    const systems: SizeSystem[] = ['descriptive', 'us', 'uk', 'eu', 'it'];
    return systems
      .filter((system) => system !== selectedSystem)
      .map((system) => ({
        system,
        value: convertSize(selectedSize, selectedSystem, system),
      }));
  };

  return (
    <div className='size-converter'>
      <h2>Size Converter</h2>

      <div className='converter-inputs'>
        <div className='select-group'>
          <label htmlFor='sizeSystem'>Select Size System:</label>
          <select
            id='sizeSystem'
            value={selectedSystem}
            onChange={(e) => {
              setSelectedSystem(e.target.value as SizeSystem);
              setSelectedSize('');
            }}
          >
            {Object.entries(sizeSystemLabels).map(([value, label]) => (
              <option key={value} value={value}>
                {label}
              </option>
            ))}
          </select>
        </div>

        <div className='select-group'>
          <label htmlFor='size'>Select Size:</label>
          <select
            id='size'
            value={selectedSize}
            onChange={(e) => setSelectedSize(e.target.value)}
          >
            <option value=''>Select a size</option>
            {availableSizes.map((size) => (
              <option key={size} value={size}>
                {size}
              </option>
            ))}
          </select>
        </div>
      </div>

      {selectedSize && (
        <div className='conversion-results'>
          <h3>Size Conversions:</h3>
          <div className='conversion-grid'>
            {getConversions()?.map(({ system, value }) => (
              <div key={system} className='conversion-item'>
                <span className='system-label'>
                  {sizeSystemLabels[system]}:
                </span>
                <span className='size-value'>{value}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default SizeConverter;
